body {
  margin: 0;
  padding: 0;
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #e8eaed;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
}

.profile-card-1703845920 {
  background: white;
  border-radius: 24px;
  padding: 60px 80px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 400px;
  width: 100%;
}

.profile-image-wrapper-1703845920 {
  width: 260px;
  height: 260px;
  border-radius: 50%;
  border: 8px solid #3b82d6;
  padding: 8px;
  background: white;
  margin-bottom: 30px;
  box-sizing: border-box;
}

.profile-image-1703845920 {
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  display: block;
}

.profile-name-1703845920 {
  font-size: 52px;
  font-weight: 600;
  color: #3b82d6;
  margin: 0 0 40px 0;
  text-align: center;
}

.follow-button-1703845920 {
  background: linear-gradient(135deg, #3b82d6 0%, #2563c4 100%);
  color: white;
  border: none;
  border-radius: 50px;
  padding: 20px 80px;
  font-size: 32px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(59, 130, 214, 0.3);
  width: 100%;
  max-width: 400px;
}

.follow-button-1703845920:hover {
  background: linear-gradient(135deg, #2563c4 0%, #1d4ed8 100%);
  box-shadow: 0 6px 20px rgba(59, 130, 214, 0.4);
  transform: translateY(-2px);
}

.follow-button-1703845920:active {
  transform: translateY(0);
  box-shadow: 0 2px 10px rgba(59, 130, 214, 0.3);
}
